var $j = $j || jQuery; //legacy support until we can get rid of everything using this var

/**
 * Global functionality for user theme pages
 * @module Global
 * @namespace NB.Liquid
 */

(function($, document, undefined){
  'use strict';

  NB.Liquid.Global = function(){
    this.init();
  };

  NB.Liquid.Global.prototype = {
    isMobileLayout: false,

    $body: null,

    init: function(){
      this.$body = $('body');

      this.addAuthTokensForAjax();
      NB.Liquid.ResponsiveEmbedWrapper.init();
      this.bindEvents();
      NB.Share.init();
      this.renderTipsy();
      this.renderDialogPopups();
      this.truncateStrings();
      new NB.Liquid.PageFeature();
      this.setTimeAgo();
      this.setTweetCharacterCounter();
      this.setAutoGrow();
      this.setCountrySelects();
      new NB.Liquid.Mobile();
      if ($('.comment_form').length) {
        new NB.Liquid.Comments();
      }
      if ($('.blog_post_page_form').length) {
        new NB.Liquid.BlogPost();
      }
      NB.Liquid.Forms.init();
      new NB.Liquid.PaginationContentLoader();
      NB.scrollToFormError.init();
      NB.Liquid.FlagContent.init();
      NB.Liquid.Donations.init();
      NB.Liquid.Follow.init();
      new NB.Liquid.SocialSlider();
    },

    bindEvents: function(){
      this.$body.on( 'click', '#flash .flash-close', this.closeFlashMessage.bind(this) );
      this.bindAjaxLinks();
      NB.EventHub.subscribe('ajaxUpdate', this.bindAjaxLinks);
    },

    renderTipsy: function(){
      if ($().tipsy) {
        $('.profile_image').tipsy( {gravity: 's'} );
      }
    },

    renderDialogPopups: function(){
      $('.ui-dialog').dialog({
        autoOpen: false,
        position: {
          my: 'top',
          at: 'top'
        },
        height: 'auto',
        width: 'auto'
      });
    },

    /**
     * Searches for all elements in the DOM that have a data attribute of "truncate" and adds the truncate plugin to
     * them
     * @method truncateStrings
     * @public
     * @type void
     */
    truncateStrings: function(){
      var $stringsToTruncate = $('[data-truncate]');

      if ($stringsToTruncate.length && $().truncate) {
        var truncateLength = $stringsToTruncate.data('truncate');

        if (truncateLength !== undefined) {
          $stringsToTruncate.truncate({
            max_length: Number(truncateLength)
          });
        }
      }
    },

    setTimeAgo: function(){
      $('abbr.timeago').timeago();
    },

    setTweetCharacterCounter: function(){
      var $tweetContent = $('.face-tweet-content');

      if ($tweetContent.length) {
        $tweetContent.each(function() {
          tweetCharacterCounter({
            tweetTextInput: $(this),
            tweetCharCountContainer: $('#face-tweet-content-charsleft')
          });
        });
      }
    },

    closeFlashMessage: function(evt){
      var $flash = $(evt.currentTarget).closest('#flash');

      $flash.fadeOut(function(){
        $flash.remove();
      });
      return false;
    },

    setAutoGrow: function(){
      var $autoGrowItems = $('.autogrow');

      if ($autoGrowItems.length) {
        $autoGrowItems.autogrow();
      }
    },

    setCountrySelects: function(){
      var set,
          self = this,
          $selects = $('#donation_billing_address_country_code, #invoice_payment_billing_address_country_code, ' +
            '#donation_work_address_country_code');

      set = function($select){
        self.showCountrySpecificFieldsForSelect($select);

        $select.on('change',function () {
          self.showCountrySpecificFieldsForSelect($(this));
        });
      };

      $selects.each(function () {
        set($(this));
      });
    },

    /**
     * Shows and hides certain fields on the donation and invoice pages depending on what country is selected
     * @method showCountrySpecificFieldsForSelect
     * @param $select {jQuery} The country select box
     * @public
     * @type void
     */
    showCountrySpecificFieldsForSelect: function($select){
      if (typeof($select) === 'undefined') {
        return;
      }
      var COUNTRIES = ['US', 'CA'],
          selectVal = $select.val(),
          $usOnly = $('.us-only'),
          $canadaOnly = $('.canada-only'),
          selectedAddressTypeClass = $select.attr('id').includes('work') ? '.work-us-or-canada' : '.us-or-canada',
          $usOrCanada = $(selectedAddressTypeClass),
          $notUsOrCanada = $('.not-us-or-canada, .work-not-us-or-canada');

      if ($.inArray(selectVal, COUNTRIES) > -1) {
        $usOrCanada.show();
        $usOrCanada.find('input').removeAttr('disabled');
        $notUsOrCanada.hide();

        if (selectVal === 'US') {
          $usOnly.show();
          $usOnly.find('select').attr('disabled', false); //handle select boxes
          $canadaOnly.hide();
          $canadaOnly.find('input').attr('disabled', true); //handle embedded inputs
        } else if (selectVal === 'CA') {
          $canadaOnly.show();
          $canadaOnly.find('input').attr('disabled', false); //handle embedded inputs
          $usOnly.hide();
          $usOnly.find('select').attr('disabled', true); //handle select boxes
        }
      } else {
        $usOrCanada.hide();
        $usOrCanada.attr('disabled', true); //handle embedded inputs
        $notUsOrCanada.show();
      }
    },

    bindAjaxLinks: function(){
      $('.ajaxForm').submitWithAjax();
      $('a.get').getWithAjax();
      $('a.post').postWithAjax();
      $('a.put').putWithAjax();
      $('a.delete').deleteWithAjax();
    },

    addAuthTokensForAjax: function(){
      $(document).ajaxSend(function(event, request, settings) {
        var getPattern = /get/i,
            isGetRequest = getPattern.test(settings.type),
            token = window.AUTH_TOKEN || window._auth_token;

        if (token === undefined || isGetRequest) {
          return;
        }

        if (_.isObject(settings.data)) {
          settings.data.authenticity_token = token;
        } else {
          settings.data += (settings.data ? '&' : '') + 'authenticity_token=' + encodeURIComponent(token);
        }
      });
    }
  };

  //if the user is viewing the mobile preview in admin, remove the tablet-and-desktop stylesheet
  if (NB.helper.getParameterByName(window.location, 'xx_browser_id') === 'mobile') {
    $('link[href *= "tablet-and-desktop"]').remove();
  }

  $(function(){
    new NB.Liquid.Global();
  });

})(jQuery, document);
